import React from 'react';
import styled from 'styled-components';

const Section = styled.section`
    padding: 300px 20px 400px 20px;
    background-image: url('main-banner.jpg');
    background-size: cover;
    background-position: center;
    position: relative;
    text-align: center;
    
`;

const Content = styled.div`
    background-color: rgba(0, 0, 0, 0.5); /* Adiciona um fundo preto transparente */
    padding: 20px;
`;

const Home = () => {
    return (
        <div>
            <Section>
                <Content>
                    <h2>Seção com Banner de Fundo</h2>
                    <p>Conteúdo da seção...</p>
                </Content>
            </Section>
        </div>
    );
};

export default Home;
