import React from 'react';
import styled from 'styled-components';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css'; // Importe o CSS principal do Swiper

// Certifique-se de remover qualquer referência ao SwiperCore

const TextSlider = styled(Swiper)`
  width: 80%;
  margin: 0 auto;
  margin-top: 20px;

  .swiper-slide {
    text-align: center;
  }
`;

const TextSlide = styled(SwiperSlide)`
  padding: 20px;
`;

const Home = () => {
  return (
    <div>
      <TextSlider
        spaceBetween={30}
        centeredSlides={true}
        autoplay={{
          delay: 2500,
          disableOnInteraction: false
        }}
        pagination={{
          clickable: true
        }}
        loop={true}
      >
        <TextSlide>
          <h3>Slide 1</h3>
          <p>Texto do slide 1</p>
        </TextSlide>
        <TextSlide>
          <h3>Slide 2</h3>
          <p>Texto do slide 2</p>
        </TextSlide>
        <TextSlide>
          <h3>Slide 3</h3>
          <p>Texto do slide 3</p>
        </TextSlide>
      </TextSlider>
    </div>
  );
};

export default Home;
